import React, { Component } from 'react';
import RandomJoke from './RandomJoke';
import Category from './Category';
import ErrorBoundry from './ErrorBoundry';
import './App.css';

export class App extends Component {

  constructor() {
    super();
    this.state = {
      randomJoke: {
        iconUrl: '',
        id: '',
        url: '',
        value: ''
      },
      categories: [],
      categoryJoke: {
        category: '',
        iconUrl: '',
        id: '',
        url: '',
        value: ''
      },
      error: null
    };
  }

  onRandomJoke = () => {
    fetch('https://api.chucknorris.io/jokes/rando')
      .then(resp => resp.json())
      .then(resp =>  {
        const randomJoke = {
          iconUrl: resp.icon_url,
          id: resp.id,
          url: resp.url,
          value: resp.value
        }
        this.setState({ randomJoke: randomJoke });
      })
      .catch(err => {
        console.log(err);
        this.setState({ error: err });
      });
  }

  onCategoryJoke = (event) => {
    const category = event.target.value;

    fetch('https://api.chucknorris.io/jokes/random?category=' + category)
      .then(resp => resp.json())
      .then(resp => {
        const categoryJoke = {
          category: resp.categories[0],
          iconUrl: resp.icon_url,
          id: resp.id,
          url: resp.url,
          value: resp.value
        }
        this.setState({ categoryJoke: categoryJoke});
      })
      .catch(err => {
        console.log(err);
        this.setState({ error: err });
      });
  }

  render() {
    const { randomJoke, categories, categoryJoke, error } = this.state;

    return (  
      <div className="App">
        <div>
          <ErrorBoundry>
            <button onClick={this.onRandomJoke}>Random Joke</button>
            <RandomJoke randomJoke={randomJoke}></RandomJoke>
          </ErrorBoundry>
          <ErrorBoundry error={error}>
            <Category categories={categories}
                      selectedCategory={this.onCategoryJoke}
                      categoryJoke={categoryJoke}>
            </Category>
          </ErrorBoundry>
        </div>
      </div>
    );
  }

  componentDidMount() {
    this.onRandomJoke();
    fetch('https://api.chucknorris.io/jokes/categories')
      .then(resp => resp.json())
      .then(categories => this.setState({categories: categories}))
      .catch(err => {
        console.log(err);
        this.setState({ error: err });
      });
  }

  componentDidUpdate() {
    console.log(this.state.categoryJoke);
  }
}

export default App;
