import React from 'react';
import './Category.css';

const Category = ({ categories }) => {
	return (
		<div>
			<ul>
				{
					categories.map(category => {
						return (
							<li>
								<button 
								  type="button"
								  className="link-button">
								    {category}
								</button>
							</li>
						);
					})
				}
			</ul>
		</div>
	);
}

export default Category;
