import React from 'react';
import './Category.css';

const Category = ({ categories, selectedCategory, categoryJoke }) => {
	return (
		<div>
			{
				categories.map((category, i) => {
					return (
						<div key={i}>
							<button 
							  type="button"
							  className="link-button"
							  value={category}
							  onClick={selectedCategory}>
							    {category}
							</button>
						</div>
					);
				})
				}
		</div>
	);
}

export default Category;
