import React, { Component } from 'react';
import RandomJoke from './RandomJoke';
import './App.css';

export class App extends Component {

  constructor() {
    super();
    this.state = {
      joke: {
        iconUrl: '',
        id: '',
        url: '',
        value: ''
      }
    };
  }

  onRetrivesJoke = () => {
    fetch('https://api.chucknorris.io/jokes/random')
      .then(resp => resp.json())
      .then(resp =>  {
        const joke1 = {
          iconUrl: resp.icon_url,
          id: resp.id,
          url: resp.url,
          value: resp.value
        }
        this.setState({ joke: joke1 });
      })
      .catch(err => console.log(err));
  }

  render() {
    const joke = this.state.joke;

    return (  
      <div className="App">
        <div>
          <button onClick={this.onRetrivesJoke}>Random Joke</button>
          <RandomJoke joke={joke}></RandomJoke>
        </div>
      </div>
    );
  }

  componentDidUpdate() {
    console.log(this.state.joke);
  }
}

export default App;
