import React, { Component } from 'react';
import RandomJoke from './RandomJoke';
import Category from './Category';
import './App.css';

export class App extends Component {

  constructor() {
    super();
    this.state = {
      randomJoke: {
        iconUrl: '',
        id: '',
        url: '',
        value: ''
      },
      categories: [],
      categoryJoke: {
        category: '',
        iconUrl: '',
        id: '',
        url: '',
        value: ''
      }
    };
  }

  onRandomJoke = () => {
    fetch('https://api.chucknorris.io/jokes/random')
      .then(resp => resp.json())
      .then(resp =>  {
        const randomJoke = {
          iconUrl: resp.icon_url,
          id: resp.id,
          url: resp.url,
          value: resp.value
        }
        this.setState({ randomJoke: randomJoke });
      })
      .catch(err => console.log(err));
  }

  onCategoryJoke = (event) => {
    console.log(event);
    // fetch('https://api.chucknorris.io/jokes/random?category=' + category)
    //   .then(resp => resp.json())
    //   .then(resp => {
    //     console.log(resp);
    //   });
  }

  render() {
    const { randomJoke, categories } = this.state;

    return (  
      <div className="App">
        <div>
          <button onClick={this.onRandomJoke}>Random Joke</button>
          <RandomJoke randomJoke={randomJoke}></RandomJoke>
          <Category categories={categories}
                    categoryJoke={this.onCategoryJoke}>
          </Category>
        </div>
      </div>
    );
  }

  componentDidMount() {
    this.onRandomJoke();
    fetch('https://api.chucknorris.io/jokes/categories')
      .then(resp => resp.json())
      .then(categories => this.setState({categories: categories}));
  }

  componentDidUpdate() {
  }
}

export default App;
