import React, { Component } from 'react';
import RandomJoke from './RandomJoke';
import './App.css';

export class App extends Component {

  constructor() {
    super();
    this.state = {
      iconUrl: '',
      id: '',
      url: '',
      value: ''
    };
  }

  onRetrivesJokes() {
    fetch('https://api.chucknorris.io/jokes/random')
      .then(resp => resp.json())
      .then(joke => this.setState({
        iconUrl: joke.icon_url,
        id: joke.id,
        url: joke.url,
        value: joke.value
      }))
      .then(data => console.log(data))
      .catch(err => console.log(err));
  }

  render() {
    return (
      <div className="App">
        <RandomJoke></RandomJoke>
      </div>
    );
  }

  componentDidMount() {
    this.onRetrivesJokes();
    console.log(iconUrl);
  }
}

export default App;
