import React, { Component } from 'react';
import RandomJoke from './RandomJoke';
import './App.css';

export class App extends Component {

  constructor() {
    super();
    this.state = {
      iconUrl: '',
      id: '',
      url: '',
      value: ''
    };
  }

  onRetrivesJoke = () => {
    fetch('https://api.chucknorris.io/jokes/random')
      .then(resp => resp.json())
      .then(joke =>  {
        this.setState({
        iconUrl: joke.icon_url,
        id: joke.id,
        url: joke.url,
        value: joke.value
        });
        console.log(joke.icon_url);
        console.log(joke.id);
        console.log(joke.url);
        console.log(joke.value);
      })
      .then(data => console.log(data))
      .catch(err => console.log(err));
  }

  render() {
    return (
      <div className="App">
        <button onClick={this.onRetrivesJoke}>Random Joke</button>
        <RandomJoke></RandomJoke>
      </div>
    );
  }

  // componentDidUpdate() {
  //   this.onRetrivesJokes();
  //   console.log(this.state);
  // }
}

export default App;
